/*Manipulando Arrays
Criar um array com um construtor
*/

// modo simples de criar um array
let myArray = [1, 2, 3]
console.log(myArray);
// modo com o construtor
let myNewArray = new Array(1, 2, 3)
console.log(myNewArray);
// Além disso, eu posso criar array com espaços vazios:
let myEmptyArray = new Array(10)
console.log(myEmptyArray); //Array com 10 espaços vazios

